[print(f"{pais['n']} {pais['o']} {pais['p']} {pais['b']}") for pais in sorted([(lambda n, o, p, b: {"n": n, "o": int(o), "p": int(p), "b": int(b)})(*input().split()) for i in range(int(input()))], key=lambda pais: (pais["o"], pais["p"], pais["b"], (lambda n: "".join([chr(ord('z') - (ord(letra) - ord('a'))) for letra in n.lower()]))(pais["n"])), reverse=True)]
